import React from 'react';

class AddMember extends React.Component {
    constructor() {
        super();    

        this.firstNameEl = React.createRef();
        this.lastNameEl = React.createRef();
        this.emailEl = React.createRef();
    }

    handler() {
        console.log("ajdhflijhf iacjdhjdahfjdnvkadbvijqhqvbqe");
        const firstName = this.firstNameEl;
        const lastName = this.lastNameEl;
        const email = this.emailEl;;

        console.log(firstName, lastName, email);

        const requestBody = {
            query: `
                mutation {
                    addMember (memberInput: {
                        firstName: ${firstName}
                        lastName: ${lastName}
                        email: ${email}
                    }) {
                        firstName
                        lastName
                        email
                    }
                }
            `
        }

        fetch('http://localhost:5000/graphql', {
            method: 'POST',
            body: JSON.stringify(requestBody),
            headers: {
                'Content-Type': 'application/json'
            }
        }).then(res => {
            return res;
        }).catch(err => {
            throw err;
        })
    }

    render() {
        return (
            <React.Fragment>
                <h1>Add New Member</h1>
                <form onSubmit={this.handler}>
                    <div className="form-item">
                        <label>First Name</label>
                        <input ref={this.firstNameEl} />
                    </div>
                    <div className="form-item">
                        <label>Last Name</label>
                        <input ref={this.lastNameEl} />
                    </div>
                    <div className="form-item">
                        <label>Email</label>
                        <input ref={this.emailEl} />
                    </div>
                    <input type="submit" />
                </form>
            </React.Fragment>
        )
    }
}

export default AddMember;
